import {useState} from 'react';
import axios from 'axios';
import "./FileUpload.css";

const FileUpload = ({contract,account,provider}) => {
  const[file,setFile]=useState(null);
  const[fileName,setFileName]=useState("No Image Selected");

  const handleSubmit=async(e)=>{
    e.preventDefault();
    if(file){
      try{
        const formData = new FormData();
        formData("file",file);

        const resFile = await axios({
          method: "post",
          url: "https://api.pinata.cloud/pinning/pinFileToIPFS",
          data: formData,
          headers: {
            pinata_api_key: `5f9c2d732d68fee6537d`,
            pinata_secret_api_key: `5f27b2a178456e6206ed177c8b81ed73737b577f63693e981af27bfe36989c02`,
            "Content-Type": "multipart/form-data",
          },
        });
        const ImgHash=`ipfs://${resFile.data.IpfsHash}`;
        //const signer = contract.connect(provider.getSigner());
        await contract.add(account,ImgHash);
        alert("Successfully Image Uploaded");
        setFileName("No Image Selected");
        setFile(null);

      }catch(e){
        alert("Unable to Upload to Pinata");
      }
    }
  }

  const retrieveFile=(e)=>{
    const data = e.target.files[0]; // files array of files object
    console.log(data);
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(data);
    reader.onloadend=()=>{
      setFile(e.target.files[0]);
    }
    setFile
  }

  return (<div className="top">
    <form className="form" onSubmit={handleSubmit}>
      <label htmlFor="file-upload" className="choose">
        Choose Image
      </label>
      <input  disabled={!account} type="file" id="file-upload" name="data" onChange={retrieveFile}></input>
      <span className="textArea">Image:{fileName}</span>
      <button type="submit" className='upload'>Upload</button>
    </form>
  </div>);
};

export default FileUpload;
